var runwayApp;

runwayApp = angular.module('runwayApp', ['ngRoute']);

runwayApp.config(function($routeProvider) {
  return $routeProvider.when('/', {
    title: 'Home',
    templateUrl: 'home',
    controller: 'homeController'
  }).when('/workspace', {
    title: 'Workspace',
    templateUrl: function(params) {
      return 'workspace?group=' + params.group;
    },
    controller: 'workspaceController'
  }).otherwise({
    redirectTo: '/'
  });
});

runwayApp.run([
  '$rootScope', '$route', function($rootScope, $route) {
    return $rootScope.$on('$routeChangeSuccess', function() {
      return document.title = $route.current.title;
    });
  }
]);
